mapper.insert:
insert into sc_user_login (id, mobile, password, 
  last_time, gmt_create, gmt_modified
  )
values (#{id,jdbcType=BIGINT}, #{mobile,jdbcType=BIGINT}, #{password,jdbcType=VARCHAR}, 
  #{lastTime,jdbcType=TIMESTAMP}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )

mapper.select:
select id
from sc_user_login
where mobile = #{mobile,jdbcType=BIGINT}

select

from sc_user_login
where mobile = #{mobile,jdbcType=BIGINT} and password = #{password,jdbcType=VARCHAR}
