mapper.select:
select

from sc_user


    open_id = #{openId,jdbcType=VARCHAR}


    mobile  like CONCAT('%', #{mobile,jdbcType=BIGINT} , '%')


    id_number like CONCAT('%', #{idNumber,jdbcType=VARCHAR} , '%')


    real_name like CONCAT('%', #{realName,jdbcType=VARCHAR} , '%')


    is_verification = #{isVerification,jdbcType=TINYINT}


    city_id =#{cityId,jdbcType=VARCHAR}


    city =#{cityId,jdbcType=BIGINT}


    email = #{email,jdbcType=VARCHAR}


order by id desc
limit #{rowIndex},#{pageSize}

SELECT COUNT(id) FROM sc_user


   open_id = #{openId,jdbcType=VARCHAR}


   mobile  like CONCAT('%', #{mobile,jdbcType=BIGINT} , '%')


   id_number like CONCAT('%', #{idNumber,jdbcType=VARCHAR} , '%')


   real_name like CONCAT('%', #{realName,jdbcType=VARCHAR} , '%')


   is_verification = #{isVerification,jdbcType=TINYINT}


   city_id =#{cityId,jdbcType=VARCHAR}


   city =#{cityId,jdbcType=BIGINT}


   email = #{email,jdbcType=VARCHAR}
